import React, { useState } from "react";
import ReactMarkdown from "react-markdown"; // Import markdown renderer
import remarkGfm from "remark-gfm"; // Import remark-gfm for GitHub flavored markdown

const Video = () => {
  const [data, setData] = useState("");
  const [inputVal, setInputVal] = useState("");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [sentimentValue, setSentimentValue] = useState(null);
  const [showFullDescription, setShowFullDescription] = useState(false); // State to manage description visibility

  const handleInputFunction = async (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      if (
        !inputVal ||
        inputVal.length < 11 ||
        !/^[a-zA-Z0-9_-]{11}$/.test(inputVal)
      ) {
        setError(true);
        setData("");
      } else {
        setError(null);
        setLoading(true);
        setData(null);
        try {
          const response = await fetch(
            `http://127.0.0.1:8000/video/${inputVal}`
          );
          if (!response.ok) {
            throw new Error(`Error: ${response.status}`);
          }
          const result = await response.json();
          setData(result);
          setSentimentValue(result["Sentiment"]);
        } catch (err) {
          setError(err.message);
        } finally {
          setLoading(false);
        }
      }
    }
  };

  const handleInputChange = (e) => {
    setInputVal(e.target.value);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    if (
      !inputVal ||
      inputVal.length < 11 ||
      !/^[a-zA-Z0-9_-]{11}$/.test(inputVal)
    ) {
      setError(true);
      setData("");
    } else {
      setError(null);
      setLoading(true);
      setData(null);
      try {
        const response = await fetch(`http://127.0.0.1:8000/video/${inputVal}`);
        if (!response.ok) {
          throw new Error(`Error: ${response.status}`);
        }
        const result = await response.json();
        setData(result);
        setSentimentValue(result["Sentiment"]);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
  };

  // Toggle full description view
  const toggleDescription = () => {
    setShowFullDescription(!showFullDescription);
  };

  return (
    <div className="home">
      <div className="form-wrapper">
        <form onSubmit={handleFormSubmit} className="form-container">
          <input
            type="text"
            id="videoId"
            placeholder="Enter Video Id"
            className="input-element"
            value={inputVal}
            onChange={handleInputChange}
            onKeyDown={handleInputFunction}
          />
          <button type="submit" className="proceed-button">
            Submit
          </button>
        </form>
        {error && <p className="error">*Please enter a valid video ID</p>}
      </div>

      {/* Show the loader while loading */}
      {loading && <div className="loader-container">Loading...</div>}

      {/* Show actual response when data is available */}
      {!loading && data && (
        <div className="output-container">
          <h1>{data["Title"]}</h1>

          {/* Description handling */}
          <div>
            <h1>Description</h1>
            <div className="text-description">
              {/* Show part of the description initially */}
              {showFullDescription
                ? data["Description"]
                : `${data["Description"].substring(0, 200)}...`}
              <button onClick={toggleDescription} className="read-more-btn">
                {showFullDescription ? "Read less" : "Read more"}
              </button>
            </div>
          </div>

          {/* Sentiment */}
          <div>
            <h2>Sentiment</h2>
            {sentimentValue && (
              <div
                className="sentiment-bar"
                style={{ backgroundColor: getSentimentColor(sentimentValue) }}
              ></div>
            )}
          </div>

          {/* Suggestions */}
          <div className="suggestions-text">
            <h1>Suggestions for improvement</h1>
            {data["Suggestions"] && (
              <ReactMarkdown remarkPlugins={[remarkGfm]}>
                {data["Suggestions"]}
              </ReactMarkdown>
            )}
            {!data["Suggestions"] && <p>No suggestions available</p>}
          </div>
        </div>
      )}
    </div>
  );
};

export default Video;
