import React, { useRef } from "react";
import "./index.css"; // Import the CSS file for styling
import Header from "../Header"; // Import the new Header component
import Logo from "../../assets/91627.png";

const cards = [
  {
    name: "Channel Analysis",
    link: "channel",
    description:
      "Provide us with a channel ID and we'll provide you with its engagement rate and statistics.",
  },
  {
    name: "Video Analysis",
    link: "video",
    description:
      "Provide us with a video ID and we'll provide you with its engagement predictions and tips for impovement.",
  },
];

const RedirectButton = (val) => {
  const navigate = useNavigate(); // Hook to access navigation functionality

  const handleRedirect = () => {
    navigate(`/${val}`); // Redirect to the home page
  };

  return <button onClick={handleRedirect} style={{ display: "none" }} />; // Hidden button for handling redirect
};

const Home = () => {
  const cardContainerRef = useRef(null);

  const handleGetStartedClick = () => {
    if (cardContainerRef.current) {
      cardContainerRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <div className="home">
      <Header />
      <div className="content-container">
        <img src={Logo} alt="central logo" className="logo-styling" />
        <h1>Unlock Insights with Our YouTube Content Analyzer</h1>
        <p>
          Analyze trends, track performance, and optimize your content strategy
          with actionable insights.
        </p>
        <button className="get-started-button" onClick={handleGetStartedClick}>
          Get Started
        </button>
      </div>

      <div className="card-container" ref={cardContainerRef}>
        {cards.map((card, index) => (
          <div className="card" key={index} onClick={RedirectButton(card.link)}>
            <div className="card-inner">
              <div className="card-front">
                <div className="card-name">{card.name}</div>
              </div>
              <div className="card-back">
                <h1>{card.name}</h1>
                <p>{card.description}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
