import React, { useState } from "react";
import "./index.css"; // Import the CSS file for styling
import Header from "../Header"; // Import the new Header component
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowRight } from "@fortawesome/free-solid-svg-icons";

const Video = () => {
  const [response, setResponse] = useState("");
  const [inputVal, setInputVal] = useState("");
  const [error, setError] = useState(false);

  const handleInputFunction = (e) => {
    if (e.key === "Enter") {
      e.preventDefault(); // Prevent the default form submission behavior on "Enter"
      if (
        !inputVal ||
        inputVal.length < 11 ||
        !/^[a-zA-Z0-9_-]{11}$/.test(inputVal)
      ) {
        setError(true);
        setResponse(""); // Clear response if there's an error
      } else {
        setError(false);
        setResponse(`Fetched details for video ID: ${inputVal}`); // Simulate a response
      }
    }
  };

  const handleInputChange = (e) => {
    setInputVal(e.target.value); // Update the input value on every change
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault(); // Prevent page reload
    if (
      !inputVal ||
      inputVal.length < 11 ||
      !/^[a-zA-Z0-9_-]{11}$/.test(inputVal)
    ) {
      setError(true);
      setResponse(""); // Clear response if there's an error
    } else {
      setError(false);
      try {
        const response = await fetch(
          `http://127.0.0.1:8000/video/${inputValue}`
        );
        if (!response.ok) {
          throw new Error(`Error: ${response.status}`);
        }
        const result = await response.json();
        setData(result); // Update data with the fetched result
      } catch (err) {
        setError(err.message); // Update error message
      } finally {
        setLoading(false); // Stop loading
      }
    }
  };

  return (
    <div className="home">
      <Header />
      <div className="form-wrapper">
        <form onSubmit={handleFormSubmit} className="form-container">
          <input
            type="text"
            id="videoId"
            placeholder="Enter Video Id"
            className="input-element"
            value={inputVal}
            onChange={handleInputChange} // Update input value on change
            onKeyDown={handleInputFunction} // Handle Enter key
          />
          <button type="submit" className="proceed-button">
            <FontAwesomeIcon icon={faArrowRight} />
          </button>
        </form>
        {error && <p className="error">*Please enter a valid video ID</p>}
      </div>
      <div className="output-container">
        {response === "" ? (
          <div>
            <h1>Nothing to Show</h1>
          </div>
        ) : (
          <div>
            <h1>{response}</h1>
          </div>
        )}
      </div>
    </div>
  );
};

export default Video;
